class GlobalNetworkGuardWidget extends StatelessWidget {
  final Widget child;
  const GlobalNetworkGuardWidget({required this.child, super.key});

  @override
  Widget build(BuildContext context) {
    final isOnline = context.select&lt;NetworkProvider, bool&gt;((p) => p.isOnline);
    return Stack(
      children: [
        child,
        if (!isOnline)
          Positioned(
            bottom: 50,
            left: 10,
            right: 10,
            child: Material(
              elevation: 6,
              borderRadius: BorderRadius.circular(12),
              color: Colors.red.shade700,
              child: Padding(
                padding: const EdgeInsets.symmetric(vertical: 12, horizontal: 16),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Row(
                      children: const [
                        Icon(Icons.wifi_off, color: Colors.white),
                        SizedBox(width: 8),
                        Text(
                          'No Internet Connection',
                          style: TextStyle(
                            color: Colors.white,
                            fontSize: 16,
                            fontWeight: FontWeight.w500,
                            decoration: TextDecoration.none,
                          ),
                        ),
                      ],
                    ),
                    TextButton(
                      onPressed: () { context.read<NetworkProvider>().recheck(); },
                      style: TextButton.styleFrom(
                        padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 8),
                        backgroundColor: Colors.white24,
                        shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(8),
                        ),
