const functions = require("firebase-functions");
const admin = require("firebase-admin");
admin.initializeApp();

const nodemailer = require("nodemailer");

// Gmail SMTP using App Password
const transporter = nodemailer.createTransport({
    service: "gmail",
    auth: {
        user: "your_email@gmail.com",
        pass: "your_app_password"
    }
});

// Generate OTP
function generateOTP() {
    return Math.floor(100000 + Math.random() * 900000).toString();
}

// Send OTP to email
exports.sendEmailOtp = functions.https.onCall(async (data, context) => {
    const email = data.email;

    if (!email) {
        throw new functions.https.HttpsError("invalid-argument", "Email required");
    }

    const otp = generateOTP();

    await transporter.sendMail({
        from: "your_email@gmail.com",
        to: email,
        subject: "Your OTP Code",
        text: `Your OTP is: ${otp}`
    });

    // Store OTP temporarily in Firestore
    await admin.firestore().collection("otp").doc(email).set({
        otp: otp,
        createdAt: admin.firestore.FieldValue.serverTimestamp()
    });

    return { success: true };
});

// Verify OTP
exports.verifyEmailOtp = functions.https.onCall(async (data, context) => {
    const { email, otp } = data;

    const doc = await admin.firestore().collection("otp").doc(email).get();

    if (!doc.exists) {
        throw new functions.https.HttpsError("not-found", "OTP not found");
    }

    const savedOtp = doc.data().otp;

    if (savedOtp !== otp) {
        throw new functions.https.HttpsError("invalid-argument", "Invalid OTP");
    }

    return { verified: true };
});

// Reset Password
exports.resetPassword = functions.https.onCall(async (data, context) => {
    const { email, newPassword } = data;

    try {
        const user = await admin.auth().getUserByEmail(email);

        await admin.auth().updateUser(user.uid, { password: newPassword });

        return { success: true };
    } catch (err) {
        throw new functions.https.HttpsError("internal", err.message);
    }
});

